pragma solidity ^0.5.0;

contract Splitter {
    address alice = msg.sender; 
    mapping (address => uint) _balance;


    constructor() public payable{}

    function sendSplit(uint amount) public payable returns(uint splitAmount){
       splitAmount = msg.value/2;
       address payable bob = 0xa52c270BC92207E595C35841c7f1f428DBC1d564;
       address payable carol = 0x77e1A72cf3C47D4c6De1D00Cdbfd38865bbc6a57;
          _balance[msg.sender] -= amount;
           bob.transfer(splitAmount);
           carol.transfer(splitAmount); 
            return msg.value; 
       }
    }
